// Check if we have base64 resume data
const resumeData = $input.item.json.resumeData;

console.log("Checking for resumeData:", !!resumeData);
console.log("ResumeData type:", typeof resumeData);

if (resumeData && resumeData.length > 0) {
  console.log("Resume data length:", resumeData.length);

  try {
    // Convert base64 string to buffer
    const buffer = Buffer.from(resumeData, 'base64');
    console.log("Buffer created, size:", buffer.length);

    // Return the item with binary data attached
    return {
      json: {
        ...$input.item.json,
        debug_buffer_size: buffer.length,
        debug_has_binary: true
      },
      binary: {
        resume: {
          data: buffer,
          mimeType: $input.item.json.resumeMimeType || 'application/pdf',
          fileName: $input.item.json.resumeFileName || 'resume.pdf',
          fileExtension: 'pdf'
        }
      }
    };
  } catch (error) {
    console.log("Buffer creation error:", error.message);
    return {
      json: {
        ...$input.item.json,
        debug_error: error.message,
        debug_failed_conversion: true
      }
    };
  }
} else {
  console.log("No resume data found or empty");
  console.log("Available keys:", Object.keys($input.item.json));
  return {
    json: {
      ...$input.item.json,
      debug_no_resume_data: true,
      debug_available_keys: Object.keys($input.item.json)
    }
  };
}

// Get first input safely
const firstInput = $input.first() || { json: {} };

// Try multiple possible fields where AI output might be stored
let aiOutput = firstInput.json.response
            || firstInput.json.output
            || firstInput.json.text
            || firstInput.json.content
            || firstInput.json.message
            || firstInput.json.result 
            || "";

// Fallback
if (!aiOutput || (typeof aiOutput === "string" && aiOutput.trim() === "")) {
  aiOutput = "I'm sorry, I couldn't generate a response. Could you please try again?";
}

// Ensure it's a string
if (typeof aiOutput !== "string") {
  aiOutput = String(aiOutput);
}

// âœ… Do NOT escape quotes/backslashes manually â€” let JSON.stringify handle it
return [{
  json: {
    response: aiOutput,
    status: "success"
  }
}];

const sessionId = $('Extract Session Data').item.json.sessionId;
console.log('Processing resume for session:', sessionId);
return $input.all();

// Check if job description exists
const jobDescription = $input.item.json.jobDescription;

if (jobDescription && jobDescription.trim().length > 0) {
  console.log("Job description found, length:", jobDescription.length);

  return [{
    json: {
      ...$input.item.json,
      hasJobDescription: true,
      jobDescriptionContent: jobDescription.trim()
    }
  }];
} else {
  console.log("No job description provided");

  return [{
    json: {
      ...$input.item.json,
      hasJobDescription: false,
      jobDescriptionContent: ""
    }
  }];
}

const sessionData = $input.first().json;
const jobDescription = sessionData.jobDescription || '';
const sessionId = sessionData.sessionId;

// ALWAYS return something, even if no job description
if (jobDescription && jobDescription.trim().length > 0) {
  return [{
    json: {
      content: `Job Description:\n\n${jobDescription.trim()}`,
      sessionId: sessionId,
      documentType: 'job_description'
    }
  }];
} else {
  // Instead of returning empty array, return a dummy item to keep flow going
  return [{
    json: {
      content: 'No job description provided',
      sessionId: sessionId,
      documentType: 'placeholder',
      skip: true
    }
  }];
}

const audioData = $input.item.json.body.audioData;
const sessionId = $input.item.json.body.sessionId;
const candidateEmail = $input.item.json.body.candidateEmail;

if (!audioData) {
    throw new Error('No audio data provided');
}

try {
    // Convert base64 to buffer
    const buffer = Buffer.from(audioData, 'base64');

    console.log('Audio buffer size:', buffer.length);

    return [{
        json: {
            sessionId,
            candidateEmail,
            audioSize: buffer.length
        },
        binary: {
            audio: {
                data: buffer,
                mimeType: 'audio/webm', // Match the frontend format
                fileName: 'recording.webm'
            }
        }
    }];
} catch (error) {
    throw new Error(`Failed to process audio data: ${error.message}`);
}

const deepgramResponse = $input.item.json;

console.log('Deepgram response:', JSON.stringify(deepgramResponse));

// Check if response has error
if (deepgramResponse.error) {
    throw new Error(`Deepgram error: ${deepgramResponse.error}`);
}

// Extract transcription from Deepgram response
let transcription = '';
let confidence = 0;

if (deepgramResponse.results && 
    deepgramResponse.results.channels && 
    deepgramResponse.results.channels[0] && 
    deepgramResponse.results.channels[0].alternatives && 
    deepgramResponse.results.channels[0].alternatives[0]) {

    const alternative = deepgramResponse.results.channels[0].alternatives[0];
    transcription = alternative.transcript || '';
    confidence = alternative.confidence || 0;
}

// If no transcription found, return appropriate message
if (!transcription.trim()) {
    return [{
        json: {
            transcription: '',
            confidence: 0,
            status: 'no_speech_detected',
            message: 'No speech detected in audio'
        }
    }];
}

return [{
    json: {
        transcription: transcription.trim(),
        confidence: confidence,
        status: 'success'
    }
}];
